-- CreateTable for OnCallPolicy (SCD Type 2 versioning)\nCREATE TABLE \"on_call_policies\" (\n    \"id\" TEXT NOT NULL PRIMARY KEY,\n    \"businessKey\" TEXT NOT NULL,\n    \"version\" INTEGER NOT NULL DEFAULT 1,\n    \"validFrom\" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    \"validTo\" DATETIME,\n    \"isCurrent\" BOOLEAN NOT NULL DEFAULT true,\n    \"funeralHomeId\" TEXT NOT NULL,\n    \"minAdvanceNoticeHours\" INTEGER NOT NULL DEFAULT 48,\n    \"maxConsecutiveWeekendsOn\" INTEGER NOT NULL DEFAULT 2,\n    \"minRestHoursAfterShift\" INTEGER NOT NULL DEFAULT 8,\n    \"backupDirectorRequired\" BOOLEAN NOT NULL DEFAULT true,\n    \"fairRotationEnabled\" BOOLEAN NOT NULL DEFAULT true,\n    \"maxOnCallPerDirectorPerQuarter\" INTEGER NOT NULL DEFAULT 13,\n    \"onCallBasePayAmount\" REAL NOT NULL DEFAULT 150,\n    \"callbackHourlyRate\" REAL NOT NULL DEFAULT 1.5,\n    \"notes\" TEXT,\n    \"createdAt\" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    \"updatedAt\" DATETIME NOT NULL,\n    \"createdBy\" TEXT NOT NULL\n);\n\n-- CreateIndex for efficient queries\nCREATE UNIQUE INDEX \"on_call_policies_businessKey_version_key\" ON \"on_call_policies\"(\"businessKey\", \"version\");\nCREATE INDEX \"on_call_policies_businessKey_isCurrent_idx\" ON \"on_call_policies\"(\"businessKey\", \"isCurrent\");\nCREATE INDEX \"on_call_policies_validFrom_validTo_idx\" ON \"on_call_policies\"(\"validFrom\", \"validTo\");\nCREATE INDEX \"on_call_policies_funeralHomeId_isCurrent_idx\" ON \"on_call_policies\"(\"funeralHomeId\", \"isCurrent\");\n